//サイズnの乗算表を出力する
void printMultiplicationTable(int n) 
{ 
    // n行を出力するループ
    for (int i = 1; i <= n; i++) 
    { 
        // n列を出力するループ
        // 列の値jを変更する
        for (int j = 1; j <= n; j++) 
        { 
            // 表の値を出力する
            // i行目のj番目の値を書き込む
            System.out.print(i*j + "   ");  
        } 
        // 次の行のために行の値iを増やす
        System.out.println();  
    } 
}
