// 単語を反転する関数
void ReverseWords(char* s) 
{ 
    char* word_begin = s; 

    // 単語の境界
    char* temp = s; 

    // 文字列を反転
    Reverse(s, temp); 

    // 各単語を反転
    while (*temp) { 
        temp++; 
        if (*temp == '\0') { 
            Reverse(word_begin, temp - 1); 
        } else if (*temp == ' ') { 
            Reverse(word_begin, temp - 1); 
            word_begin = temp + 1; 
        } 
    } 
} 

