function addNum(num) {
  var screen = document.getElementById('calculator-screen');
  screen.value += num;
}

function calculate(sign) {
  var screen = document.getElementById('calculator-screen');
  var result;
  switch(sign) {
    case '+':
      result = eval(screen.value);
      break;
    case '-':
      result = eval(screen.value);
      break;
    case '*':
      result = eval(screen.value);
      break;
    case '/':
      result = eval(screen.value);
      break;
    case '=':
      result = eval(screen.value);
      break;
  }
  screen.value = result;
}

