'//貪欲アルゴリズムを使用した文字列マッチングのプログラム

#include<stdio.h>
#include<stdbool.h>

//与えられた文字列とパターンの一致を見つける関数
bool stringMatching(char string[], char pattern[])
{
	//与えられた文字列の長さを計算する
	int n = strlen(string);

	//与えられたパターンの長さを計算する
	int m = strlen(pattern);

	//文字列とパターンのインデックス
	int i = 0、j = 0;

	//一致するための貪欲ループ
	while(i < n && j < m) {
		if(string[i] == pattern[j]) {
			i++;
			j++;
		}
		//文字列の1つ前に戻る
		else {
			i = i - j + 1;
			j = 0;
		}
	}

	//パターンが見つかったかどうかを確認する
	if(j == m)
		return true;

	return false;
}

//ドライバーコード
int main()
{
	char string[] = "ababcab";
	char pattern[] = "cab";

	bool flag = stringMatching(string, pattern);

	if (flag)
		printf("パターンが一致します");
	else
		printf("パターンが一致しません");

	return 0;
}'

// バブルソートアルゴリズムを実装するプログラム
#include <stdio.h>

void bubbleSort(int arr[], int n)
{
    int i, j;
    for (i = 0; i < n-1; i++)

    // 最後のi個の要素はすでに正しい位置にある
    for (j = 0; j < n-i-1; j++)
        if (arr[j] > arr[j+1])
            swap(&arr[j], &arr[j+1]);
}

void swap(int *a, int *b)
{
    int temp = *a;
    *a = *b;
    *b = temp;
}

// ドライバープログラム
int main()
{
    int arr[] = {64, 34, 25, 12, 22, 11, 90};
    int n = sizeof(arr)/sizeof(arr[0]);
    bubbleSort(arr, n);
    return 0;
}
