'// 2つの配列の共通部分を見つけるアルゴリズム

function intersection(arr1, arr2) {
    // 共通部分の要素を格納するための空の配列を作成
    let result = [];

    // arr1のすべての要素をループする
    for (let i = 0; i < arr1.length; i++) {
        // arr2のすべての要素をループする
        for (let j = 0; j < arr2; j++){
            // arr1とarr2の要素が一致するかどうかを確認する
            if (arr1[i] === arr2[j]) {
                // 一致した場合、要素をresult配列に追加する
                result.push(arr1[i]);
            }
        }
    }

    // 結果の配列を返す
