var arr = [3, 9, 8, 5, 1, 11];

// 未整列の配列を引数とする関数を作成する
function sortNum(arr) {

// 整列された結果を格納する空の配列を作成する
 let sortedArr = [];

// 配列内の最大値を取得する
 let highestNum = Math.max(...arr);

// すべての数値が整列されるまでループする
 while (arr.length > 0) {
    // 配列を反復処理し、最大値のインデックスを見つける
    let highestNumIdx = arr.indexOf(highestNum)

    // 最大値を整列された配列に後から追加する
    sortedArr.push(arr[highestNumIdx]);

    // 元の配列から最大値を削除する
    arr.splice(highestNumIdx, 1);

    // 最大値をより小さい数値で置き換える
    highestNum = Math.max(...arr);
}

// 整列された配列を返す
 return sortedArr;
}

