'class Solution:
    def checkValidString(self, s: str) -> bool:
        open_stack = []
        star_stack = []
        for i, char in enumerate(s):

            # "("があればopen_stackに追加し、"*"があればstar_stackに追加する
            if char == "(":
                open_stack.append(i)
            elif char == "*":
                star_stack.append(i)

            # 現在の開き括弧または星をチェックし、対応する閉じ括弧があればpopする
            # 対応する括弧が見つからない場合、式は無効です。
            elif char == ")":
                if open_stack:
                    open_stack.pop()
                elif star_stack:
                    star_stack.pop()
                else:
                    return False

        # 残りのスタックの長さをチェックする
        while open_stack and star_stack:
            o = open_stack.pop()
            s = star_stack.pop()

            if o > s:
                return False

        return len(open_stack) == 0'
