// 挿入ソートの実装
static void sort(int arr[]) {
    int n = arr.length;
    for (int i = 1; i < n; ++i) {
        int key = arr[i];
        int j = i - 1;

        /* arr[0..i-1]の要素で、keyより大きいものを、
           現在の位置より1つ前に移動する */
        while (j >= 0 && arr[j] > key) {
            arr[j + 1] = arr[j];
            j = j - 1;
        }
        arr[j + 1] = key;
    }
}

// ドライバーコード
